import React, { useState } from 'react'
import './App.css'
const BgChange = () => {
    const [color, setColor] = useState('red')
  return (
    <div className='body' style={ {backgroundColor:color}}>
      <h1 > Background Change</h1>
      <button onClick={()=> setColor('green')}> bg Change</button>
      <button onClick={()=> setColor('yellow')}> bg Change yellow</button>
    </div>
  )
}

export default BgChange;
